import React, { useState } from 'react';
import Row from './Row';
import './rowList.css';

function RowList() {
  const [numRows, setNumRows] = useState(5); // Initial number of rows

  const addRow = () => {
    setNumRows(numRows + 1);
  };

  const rows = Array.from({ length: numRows }, (_, index) => <Row key={index} />);

  return (
    <div className="rowList">
      {rows}
      <button onClick={addRow} style={{ marginTop: '10px' }}>Add Row</button> {/* Dummy button */}
    </div>
  );
}

export default RowList;
