import React from 'react';
import './Row.css';

function Row({ title, genre, releaseDate, htlb, timePlayed, status, score, filterText }) {
  // Example logic to filter rows, adjust as needed based on your actual data
  const isVisible = filterText === '' || title.includes(filterText);

  if (!isVisible) return null;

  return (
    <div className="row">
      <div className="gameInfo"> 
        <span>{title}</span>
        <span>{genre}</span>
        <span>{releaseDate}</span>
        <span>{htlb}</span>
        <span>{timePlayed}</span>
        <span>{status}</span>
      </div>
      <span className="score">{score}</span>
    </div>
  );
}

export default Row;
