import React, { useState } from 'react';
import './Row.css';

function Row({id, title, genre, releaseDate, htlb, timePlayed, status, score }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editedTitle, setEditedTitle] = useState(title);

  const handleDoubleClick = () => {
    setIsEditing(true);
  };

  const handleChange = (e) => {
    setEditedTitle(e.target.value);
  };

  const handleBlur = () => {
    setIsEditing(false);
  };

  return (
    <div className="row" onClick={() => onRowClick({ id, title, genre, releaseDate, htlb, timePlayed, status, score })}>
      <div className="gameInfo"> 
        <span >{editedTitle}</span>
        <span>{genre}</span>
        <span>{releaseDate}</span>
        <span>{htlb} hs</span>
        <span>{timePlayed} hs</span>
        <span>{status} </span>
      </div>
      <span className="score">{score}</span>
    </div>
  );
}

export default Row;
