import React, { useState, useEffect } from 'react';
import Row from './Row';
import RowFilter from './RowFilter';
import './RowList.css';

function RowList() {
  const [rows, setRows] = useState([]);
  const [sortKey, setSortKey] = useState(null);

  useEffect(() => {
    // Initialize with 5 rows
    const initialRows = Array.from({ length: 5 }, (_, index) => createRow(index));
    setRows(initialRows);
  }, []);

  const createRow = (index) => ({
    title: `title ${index}`,
    genre: `genre ${index}`,
    releaseDate: `releaseDate ${index}`,
    htlb: `htlb ${index}`,
    timePlayed: `timePlayed ${index}`,
    status: `status ${index}`,
    score: `score ${index}`
  });

  const addRow = () => {
    const newRows = [...rows, createRow(rows.length)];
    if (sortKey) {
      setRows(sortRows(newRows, sortKey));
    } else {
      setRows(newRows);
    }
  };

  const handleSortChange = (key) => {
    setSortKey(key);
    setRows(sortRows([...rows], key));
  };

  const sortRows = (rows, key) => {
    return rows.sort((a, b) => (a[key] > b[key] ? 1 : -1));
  };

  const getSortedRows = () => {
    return rows.map((row, index) => (
      <Row key={index} {...row} />
    ));
  };

  return (
    <div className="rowList">
      <RowFilter onSortChange={handleSortChange} />
      {getSortedRows()}
      <button onClick={addRow} style={{ marginTop: '10px' }}>Add Row</button>
    </div>
  );
}

export default RowList;
