import React, { useState } from 'react';
import './Row.css';

function Row({ title, genre, releaseDate, htlb, timePlayed, game_status, score }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editedTitle, setEditedTitle] = useState(title);

  const handleDoubleClick = () => {
    setIsEditing(true);
  };

  const handleChange = (e) => {
    setEditedTitle(e.target.value);
  };

  const handleBlur = () => {
    setIsEditing(false);
  };

  return (
    <div className="row">
      <div className="gameInfo"> 
        {isEditing ? (
          <input
            type="text"
            value={editedTitle}
            onChange={handleChange}
            onBlur={handleBlur}
            autoFocus
          />
        ) : (
          <span onDoubleClick={handleDoubleClick}>{editedTitle}</span>
        )}
        <span>{genre}</span>
        <span>{releaseDate}</span>
        <span>{htlb} hs</span>
        <span>{timePlayed} hs</span>
        <span>{gameStatus}</span>
      </div>
      <span className="score">{score}</span>
    </div>
  );
}

export default Row;
