import React, { useState, useEffect } from 'react';
import Row from './Row';
import RowFilter from './RowFilter';
import './RowList.css';

function RowList() {
  const [rows, setRows] = useState([]);

  useEffect(() => {
    const initialRows = Array.from({ length: 5 }, (_, index) => createRow(index));
    setRows(initialRows);
  }, []);

  const createRow = (index) => ({
    title: `title ${index}`,
    genre: `genre ${index}`,
    releaseDate: `releaseDate ${index}`,
    htlb: `htlb ${index}`,
    timePlayed: `timePlayed ${index}`,
    status: `status ${index}`,
    score: `score ${index}`
  });

  const addRow = () => {
    setRows([...rows, createRow(rows.length)]);
  };

  const getRows = () => {
    return rows.map((row, index) => (
      <Row key={index} {...row} />
    ));
  };

  return (
    <div className="rowList">
      <RowFilter />
      {getRows()}
      <button onClick={addRow} style={{ marginTop: '10px' }}>Add Row</button>
    </div>
  );
}

export default RowList;
