import React, { useState } from 'react';
import Row from './Row';
import Filter from './Filter';
import RowFilter from './RowFilter';
import './RowList.css';

function RowList() {
  const [numRows, setNumRows] = useState(5); // Initial number of rows
  const [filterText, setFilterText] = useState('');
  const [sortKey, setSortKey] = useState(null);

  const addRow = () => {
    setNumRows(numRows + 1);
  };

  const handleFilterChange = (text) => {
    setFilterText(text);
  };

  const handleSortChange = (key) => {
    setSortKey(key);
  };

  const getSortedRows = () => {
    const rows = Array.from({ length: numRows }, (_, index) => ({
      title: `title ${index}`,
      genre: `genre ${index}`,
      releaseDate: `releaseDate ${index}`,
      htlb: `htlb ${index}`,
      timePlayed: `timePlayed ${index}`,
      status: `status ${index}`,
      score: `score ${index}`,
    }));

    if (sortKey) {
      rows.sort((a, b) => (a[sortKey] > b[sortKey] ? 1 : -1));
    }

    return rows.map((row, index) => (
      <Row key={index} filterText={filterText} {...row} />
    ));
  };

  return (
    <div className="rowList">
      <RowFilter onSortChange={handleSortChange} />
      {getSortedRows()}
      <Filter onFilterChange={handleFilterChange} />
      <button onClick={addRow} style={{ marginTop: '10px' }}>Add Row</button>
    </div>
  );
}

export default RowList;
