import React, { useState } from 'react';
import './Row.css';
import './RowFilter.css';

function RowFilter({ onSortChange }) {
  const [clickedIndex, setClickedIndex] = useState(null);

  const handleClick = (index, item) => {
    setClickedIndex(index);
    onSortChange(item); // Trigger sort change
  };

  const items = ['title', 'genre', 'releaseDate', 'htlb', 'timePlayed', 'status'];

  return (
    <div className="rowFilter">
      <div className="gameInfo">
        {items.map((item, index) => (
          <span
            key={index}
            className={`gameInfo-item ${clickedIndex === index ? 'highlight' : ''}`}
            onClick={() => handleClick(index, item)}
          >
            {item}
          </span>
        ))}
      </div>
      <span className={`score ${clickedIndex === items.length ? 'highlight' : ''}`} onClick={() => handleClick(items.length, 'score')}>
        score
      </span>
    </div>
  );
}

export default RowFilter;
