import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';

Modal.setAppElement('#root');  // To prevent accessibility issues with screen readers

function EditGameModal({ isOpen, onRequestClose, gameData, onSave }) {
  const [editedGame, setEditedGame] = useState(gameData);

  useEffect(() => {
    setEditedGame(gameData);
  }, [gameData]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedGame({ ...editedGame, [name]: value });
  };

  const handleSave = () => {
    onSave(editedGame);
    onRequestClose();
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Edit Game"
      className="modal"
      overlayClassName="overlay"
    >
      <h2>Edit Game</h2>
      <form>
        <label>
          Title:
          <input
            type="text"
            name="title"
            value={editedGame.title}
            onChange={handleChange}
          />
        </label>
        <label>
          Genre:
          <input
            type="text"
            name="genre"
            value={editedGame.genre}
            onChange={handleChange}
          />
        </label>
        <label>
          Release Date:
          <input
            type="date"
            name="releaseDate"
            value={editedGame.releaseDate}
            onChange={handleChange}
          />
        </label>
        <label>
          Time Played:
          <input
            type="number"
            name="timePlayed"
            value={editedGame.timePlayed}
            onChange={handleChange}
          />
        </label>
        <label>
          Status:
          <input
            type="text"
            name="gameStatus"
            value={editedGame.gameStatus}
            onChange={handleChange}
          />
        </label>
        <label>
          Score:
          <input
            type="number"
            name="score"
            value={editedGame.score}
            onChange={handleChange}
          />
        </label>
        <label>
          HLTB:
          <input
            type="number"
            name="hltb"
            value={editedGame.hltb}
            onChange={handleChange}
          />
        </label>
        <button type="button" onClick={handleSave}>
          Save
        </button>
        <button type="button" onClick={onRequestClose}>
          Cancel
        </button>
      </form>
    </Modal>
  );
}

export default EditGameModal;
