import React, { useState, useEffect } from 'react';
import Row from './Row';
import RowFilter from './RowFilter';
import './RowList.css';

import axios from 'axios';

function RowList() {
  const [rows, setRows] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8080/api/games')
        .then(response => {
          console.log("respuesta al get: ", response.data);
          setRows(response.data);
        })
        .catch(error => {
          console.error('Error fetching game list: ', error);
        });
  }, []);

  // const createRow = (index) => ({
  //   title: `title ${index}`,
  //   genre: `genre ${index}`,
  //   releaseDate: `releaseDate ${index}`,
  //   htlb: `htlb ${index}`,
  //   timePlayed: `timePlayed ${index}`,
  //   status: `status ${index}`,
  //   score: `score ${index}`
  // });

  const addRow = () => {
    setRows([...rows, createRow(rows.length)]);
  };

  const getRows = () => {

    return rows.map((row, index) => (
     // <Row key={index} {...row} />

      <Row 
        key={index}
        title={row.title}
        genre={row.genre}
        releaseDate={row.releaseDate} 
        htlb={row.htlb} 
        timePlayed={row.timePlayed} 
        status={row.game_status} // Aquí debe coincidir con el nombre de la propiedad en la API 
        score={row.score} 
      />
    ));
  };

  return (
    <div className="rowList">
      <RowFilter />
      {getRows()}
      <button onClick={addRow} style={{ marginTop: '10px' }}>Add Row</button>
    </div>
  );
}

export default RowList;
